def gain(w,mix,T0,Temp2,Temp3):
    '''Compute the amplitude gain in an isotopic CO2 laser.

    :param numpy.ndarray w: array of angular frequencies (rad/s)
    :param numpy.ndarray mix: [626,628,828,636,638,838,N2,He] as partial pressures (atm)
    :param double T0: gas temperature (K)
    :param double Temp2: equilibrium vibrational temperature of nu1 and nu2 modes (K)
    :param double Temp3: = equilibrium vibrational temperature of n3 mode (K)

    :returns: array of complex gains in inverse meters'''

    p_CO2 = np.sum(mix[:6])
    p_N2 = mix[6]
    p_He = mix[7]
    T2 = 1e-6 / (np.pi*7.61*750*(p_CO2+0.733*p_N2+0.64*p_He)) # transition dipole dephasing time, s
    tauR = 1e-7 / (750*(1.3*p_CO2+1.2*p_N2+0.6*p_He)) # rotational thermalization time, s
    gamma = 1.0 / T2 # Lorentzian HWHM
    N = 2.7e25 * mix[:6]

    # Normalized populations of rotational sublevels
    # nop[isotopologue,band,level]
    j = np.linspace(0.0,60.0,61)
    eps = C.h*B/(C.k*T0)
    nop = np.einsum('ijk,l',eps,2*j+1) * np.exp(np.einsum('ijk,l',-eps,j*(j+1)))
    # for symmetric molecules (not 628 or 638) we double it
    nop[0,...] *= 2
    nop[2:4,...] *= 2
    nop[5:,...] *= 2

    # Transition frequencies, Hz
    # v[isotopologue,band,branch,j]
    v = np.zeros((6,4,4,61))
    v[:,:,0,:] = V[:,:,0,np.newaxis] + np.einsum('ij,k',B[:,:,0],(j-1)*(j+0)) - np.einsum('ij,k',B[:,:,1],j*(j+1)) #10P
    v[:,:,1,:] = V[:,:,0,np.newaxis] + np.einsum('ij,k',B[:,:,0],(j+1)*(j+2)) - np.einsum('ij,k',B[:,:,1],j*(j+1)) #10R
    v[:,:,2,:] = V[:,:,1,np.newaxis] + np.einsum('ij,k',B[:,:,0],(j-1)*(j+0)) - np.einsum('ij,k',B[:,:,2],j*(j+1)) #9P
    v[:,:,3,:] = V[:,:,1,np.newaxis] + np.einsum('ij,k',B[:,:,0],(j+1)*(j+2)) - np.einsum('ij,k',B[:,:,2],j*(j+1)) #9R

    # Transition cross-sections, m^2
    # sigma[isotopologue,band,branch,j]
    v_small = 1.0
    sigma = (C.c/(v_small+v))**2 * A / (4*np.pi*gamma)

    # Initial populations and polarizations
    Nvib0 = np.zeros((6,4,3)) # vibrational population density (equilibrium)
    Nrot = np.zeros((6,4,3,61)) # rotational population density
    Dn = np.zeros((6,4,4,61)) # Population inversions (rotational transitions)

    # Vibrational level population densities in thermal equilibrium - see Witteman p. 71 and Nevdakh 2007
    # partition function
    Q = 1.0 / ( (1.0-np.exp(-1920.0/Temp2))*(1.0-np.exp(-960.0/Temp2))**2*(1.0-np.exp(-3380.0/Temp3)) )
    # reg
    Nvib0[:,0,0] = N*np.exp(-3380.0/Temp3)/Q                       # upper
    Nvib0[:,0,1] = N*np.exp(-1920.0/Temp2)/Q                       # lower I (10 um)
    Nvib0[:,0,2] = N*np.exp(-2.0*960.0/Temp2)/Q                    # lower II (9 um)
    # hot-e
    Nvib0[:,1,0] = N*np.exp(-3380.0/Temp3)*np.exp(-960.0/Temp2)/Q  # upper
    Nvib0[:,1,1] = N*np.exp(-(1920.0+960.0)/Temp2)/Q               # lower I (10 um)
    Nvib0[:,1,2] = N*np.exp(-3.0*960.0/Temp2)/Q                    # lower II (9 um)
    # hot-f
    Nvib0[:,2,0] = N*np.exp(-3380.0/Temp3)*np.exp(-960.0/Temp2)/Q  # upper
    Nvib0[:,2,1] = N*np.exp(-(1920.0+960.0)/Temp2)/Q               # lower I (10 um)
    Nvib0[:,2,2] = N*np.exp(-3.0*960.0/Temp2)/Q                    # lower II (9 um)
    # seq
    Nvib0[:,3,0] = N*np.exp(-2.0*3380.0/Temp3)/Q                   # upper
    Nvib0[:,3,1] = N*np.exp(-1920.0/Temp2)*np.exp(-3380.0/Temp3)/Q    # lower I (10 um)
    Nvib0[:,3,2] = N*np.exp(-2.0*960.0/Temp2)*np.exp(-3380.0/Temp3)/Q # lower II (9 um)

    Nrot = nop * Nvib0[...,np.newaxis]

    # population inversions
    for ba in range(4):
        Dn[:,ba,0,1:]  = Nrot[:,ba,0,:-1] - Nrot[:,ba,1,1:] # 10P
        Dn[:,ba,1,:-1] = Nrot[:,ba,0,1:] - Nrot[:,ba,1,:-1] # 10R
        Dn[:,ba,2,1:]  = Nrot[:,ba,0,:-1] - Nrot[:,ba,2,1:] # 9P
        Dn[:,ba,3,:-1] = Nrot[:,ba,0,1:] - Nrot[:,ba,2,:-1] # 9R

    ans = np.zeros(w.shape).astype(np.complex)
    for i in range(w.shape[0]):
        dw = w[i] - 2*np.pi*v
        ans[i] = np.sum(gamma * sigma * Dn * (gamma - 1j*dw) / (dw**2 + gamma**2))
    return np.conj(ans)
